        console.log("componentDidUpdate");
    }
    componentDidUpdate(prevProps, prevState) {
            console.log("componentDidUpdate");
        }
        //cwum اختصار للكود التالي
    componentWillUnmount() {

    }
    render() {
        console.log("render");
        return ( <
            div >
            <
            Fragment >
            <
            li > Home < /li> <
            li > About < /li> < /
            Fragment > <
            button onClick = { this.updateData } > Update < /button> <
            input type = "text"
            ref = { this.inputRef }
            / > <
            input type = "text"
            ref = { this.callbackRefFun }
            / > < /
            div >
        );
    }
}

export default Home;
